INSERT INTO `okvad` VALUES ('01.21', 'Выращивание виноградаЭта группировка включает:- выращивание винных и столовых сортов винограда на виноградникахЭта группировка не включает:- производство вина, см. 11.02', 'A');
INSERT INTO `okvad` VALUES ('01.22', 'Выращивание тропических и субтропических культурЭта группировка включает:- выращивание тропических и субтропических культур, таких как авокадо, бананы и овощные бананы, финики, фиги (инжир), манго, папайя, ананасы, прочие тропические и субтропические фрукты', 'A');
INSERT INTO `okvad` VALUES ('01.23', 'Выращивание цитрусовых культурЭта группировка включает:- выращивание цитрусовых культур, таких как: грейпфруты и помело, лимоны и лаймы, апельсины, мандарины всех видов, прочие цитрусовые культуры', 'A');
INSERT INTO `okvad` VALUES ('01.24', 'Выращивание семечковых и косточковых культурЭта группировка включает:- выращивание таких семечковых и косточковых плодов, как: яблоки, абрикосы, вишня и черешня, персики и нектарины, груши и айва, сливы и терн, прочие семечковые и косточковые плоды', 'A');
