INSERT INTO `okvad` VALUES ('02.10.11', 'Выращивание посадочного материала лесных растений (саженцев, сеянцев)', 'A');
INSERT INTO `okvad` VALUES ('02.10.19', 'Выращивание прочей продукции лесопитомниками', 'A');
