INSERT INTO `okvad` VALUES ('08.12', 'Разработка гравийных и песчаных карьеров, добыча глины и каолинаЭта группировка включает:- добычу и промывку гравия и песка для промышленности и строительства;- дробление и измельчение гравия;- карьерную разработку песка;- добычу глины (включая огнеупорную) и каолинаЭта группировка не включает:- добычу битуминозного песка, см. 06.10', 'B');
