INSERT INTO `okvad` VALUES ('09.10', 'Предоставление услуг в области добычи нефти и природного газаЭта группировка включает:- предоставление услуг в части добычи нефти и газа за вознаграждение или на договорной основе, в том числе: проведение поисково-разведочных работ перед осуществлением добычи нефти или газа, например с использованием традиционных методов разведки, таких как проведение геологических исследований в местах будущих разработок;- направленное бурение и повторное бурение, ударное бурение, монтаж буровой установки на месте, ремонт и демонтаж, цементирование обсадных труб нефтяных и газовых скважин, откачка скважин, заглушка и консервация скважин и т.д.;- сжижение и обогащение природного газа на месте добычи для последующей перевозки;- услуги по дренажу и откачке воды за вознаграждение или на договорной основе, пробное бурение на месте предполагаемой добычи нефти или газаЭта группировка также включает:- противопожарные услуги на месторождениях нефти и газа', 'B');
