INSERT INTO `okvad` VALUES ('12.00', 'Производство табачных изделийЭта группировка включает:- производство изделий из табака, махорки и заменителей табака: сигарет, папирос, сигар, сигарилл, курительного тонкорезаного табака, трубочного табака, жевательного табака, сосательного табака, нюхательного табака, табака для кальяна, курительной и нюхательной махорки- производство гомогенизированного или восстановленного табакаЭта группировка также включает:- нарезку и повторную сушку табачных изделийЭта группировка не включает:- выращивание и первичную обработку табака и махорки, см. 01.15, 01.63', 'C');
