INSERT INTO `okvad` VALUES ('13.95', 'Производство нетканых текстильных материалов и изделий из них, кроме одеждыЭта группировка включает:- все виды работ, связанных с производством текстиля и текстильной продукции, не включенные в группировки 13 или 14, которые описывают большое количество процессов обработки и большой ассортимент продукции, в том числе производство нетканых материалов и нетканых ватинов', 'C');
