INSERT INTO `okvad` VALUES ('14.31', 'Производство вязаных и трикотажных чулочно-носочных изделийЭта группировка включает:- производство чулочно-носочных изделий, включая носки, трико и колготки;- изготовление вязаных и трикотажных чулочно-носочных изделий по индивидуальному заказу населения', 'C');
