INSERT INTO `okvad` VALUES ('15.20.12', 'Производство обуви с верхом из резины и пластмассы, кроме водопроницаемой', 'C');
INSERT INTO `okvad` VALUES ('15.20.13', 'Производство обуви с верхом из кожи, кроме спортивной обуви, обуви с защитным металлическим подноском и различной специальной обуви', 'C');
INSERT INTO `okvad` VALUES ('15.20.14', 'Производство обуви с верхом из текстильных материалов, кроме спортивной обуви', 'C');
