INSERT INTO `okvad` VALUES ('15.20.31', 'Производство обуви с защитным металлическим подноском', 'C');
INSERT INTO `okvad` VALUES ('15.20.32', 'Производство деревянной и различной специальной обуви', 'C');
INSERT INTO `okvad` VALUES ('15.20.4', 'Производство деталей обуви из кожи; вкладных стелек, подпяточников и аналогичных изделий; производство гетр, гамашей и аналогичных изделий', 'C');
INSERT INTO `okvad` VALUES ('15.20.41', 'Производство деталей обуви из кожи; вкладных стелек, подпяточников и аналогичных изделий', 'C');
