INSERT INTO `okvad` VALUES ('23.61', 'Производство изделий из бетона для использования в строительствеЭта группировка включает:- производство готовых строительных изделий из бетона, цемента и искусственного камня: плиток, плит, кирпича, щитов, листов, панелей, труб, столбов и т.п.;- производство сборных строительных конструкций из цемента, бетона и искусственного камня', 'C');
