INSERT INTO `okvad` VALUES ('23.65', 'Производство изделий из асбестоцемента и волокнистого цементаЭта группировка включает:- производство строительных материалов из растительного сырья (древесной шерсти, соломы, тростника, камыша), смешанного с цементом, гипсом или прочими минеральными связующими веществами;- производство изделий из асбестоцемента и волокнистого цемента с волокнами целлюлозы или аналогичных материалов: гофрированных листов, прочих листов, панелей, черепицы, муфт, труб, трубок, резервуаров, чанов, моек, раковин, сосудов, мебели, оконных коробок и т.п.', 'C');
