INSERT INTO `okvad` VALUES ('27.5', 'Производство бытовых приборовЭта группировка включает:- производство электрических приборов и электрической бытовой техники, таких как: вентиляторы, пылесосы, полотеры, кухонные приборы, кухонное оборудование, стиральные машины, холодильники, морозильные камеры и шкафы, а также прочей электрической и неэлектрической бытовой техники, такой как посудомоечные машины, водонагреватели и мусоросборникиЭта группировка также включает:- производство приборов, работающих на электричестве, газе и на прочих источниках энергии', 'C');
