INSERT INTO `okvad` VALUES ('27.90.1', 'Производство ускорителей заряженных частицЭта группировка также включает:- производство источников частиц, систем обеспечения работы и управления ускорителями;- производство оборудования для транспортирования и коммутации пучка в ускорителях заряженных частиц;- производство оборудования для ускорителей заряженных частиц для физических исследований', 'C');
