INSERT INTO `okvad` VALUES ('28.96', 'Производство машин и оборудования для переработки пластмасс и резиныЭта группировка включает:- производство машин для переработки мягкой резины или пластмассы, а также для производства изделий из этих материалов, включая штамповочные и формовочные машины, аппараты для производства пневматических шин или восстановления автомобильных покрышек и прочих видов машин для производства определенных резиновых или пластмассовых изделий', 'C');
