INSERT INTO `okvad` VALUES ('45.3', 'Торговля автомобильными деталями, узлами и принадлежностямиЭта группировка включает:- оптовую и розничную торговлю всеми видами запасных частей, компонентов, инструментов и принадлежностей для транспортных средств, такими как: резиновые покрышки и камеры, свечи зажигания, аккумуляторы, осветительное оборудование и прочие детали', 'G');
