INSERT INTO `okvad` VALUES ('46.12', 'Деятельность агентов по оптовой торговле топливом, рудами, металлами и химическими веществамиЭта группировка включает:- оптовую торговлю, осуществляемую агентами, занимающимися продажей топлива, руд, металлов, химических веществ, включая удобренияЭта группировка не включает:- оптовую торговлю от собственного имени, см. 46.2 - 46.9;- розничную торговлю, осуществляемую комиссионными агентами вне магазинов, см. 47.99', 'G');
