INSERT INTO `okvad` VALUES ('49.41', 'Деятельность автомобильного грузового транспортаЭта группировка включает:- все виды перевозок грузов автомобильным транспортом по автомобильным дорогам: опасных грузов, крупногабаритных и/или тяжеловесных грузов, грузов в контейнерах и транспортных пакетах, скоропортящихся грузов, массовых навалочных грузов, сельскохозяйственных грузов, грузов строительной отрасли, грузов промышленных предприятий, прочих грузовЭта группировка также включает:- аренду грузовых автомобилей с водителем;- деятельность по перевозке грузов транспортными средствами, приводимыми в движение людьми или животными в качестве тягловой силы', 'H');
