INSERT INTO `okvad` VALUES ('52.22', 'Деятельность вспомогательная, связанная с водным транспортомЭта группировка включает:- деятельность, связанную с перевозкой водным транспортом пассажиров, животных или грузов, включая: деятельность терминальных мощностей, таких как порты и пирсы, эксплуатацию шлюзов и т.д., навигационное обеспечение, лоцманскую проводку судов и проводку судов к причалу;- деятельность по разгрузке судов лихтером и спасению судов;- деятельность маяковЭта группировка не включает:- погрузочно-разгрузочные работы, см. 52.24;- деятельность причалов для прогулочных судов, см. 93.29', 'H');
