INSERT INTO `okvad` VALUES ('52.22.21', 'Деятельность инфраструктуры речных портов и гидротехнических сооружений', 'H');
INSERT INTO `okvad` VALUES ('52.22.22', 'Обеспечение судоходства по внутренним водным путям, в том числе лоцманская проводка судов', 'H');
INSERT INTO `okvad` VALUES ('52.22.23', 'Деятельность по постановке судов к причалу, осуществление швартовых операций в речных портах на внутреннем водном транспорте', 'H');
INSERT INTO `okvad` VALUES ('52.22.24', 'Деятельность по навигационному обеспечению судоходства на внутреннем водном транспортеЭта группировка включает:- деятельность береговых служб, систем управления движением судов', 'H');
INSERT INTO `okvad` VALUES ('52.22.25', 'Деятельность аварийно-спасательная и судоподъемная на внутреннем водном транспорте', 'H');
INSERT INTO `okvad` VALUES ('52.22.26', 'Снабженческое (шипчандлерское) обслуживание судов, включая бункеровку судов топливом, обслуживание судов в период стоянки в портах: агентирование судов, обследовательское (сюрвейерское) обслуживание судов на внутреннем водном транспорте', 'H');
INSERT INTO `okvad` VALUES ('52.22.27', 'Производство водолазных работ по обслуживанию судов на внутреннем водном транспорте', 'H');
INSERT INTO `okvad` VALUES ('52.22.28', 'Деятельность ледокольного флота на внутреннем водном транспорте', 'H');
INSERT INTO `okvad` VALUES ('52.22.29', 'Деятельность вспомогательная, связанная с внутренним водным транспортом, прочая, не включенная в другие группировки', 'H');
INSERT INTO `okvad` VALUES ('52.23', 'Деятельность вспомогательная, связанная с воздушным и космическим транспортомЭта группировка включает:- деятельность, связанную с перевозкой воздушным и космическим транспортом пассажиров, космонавтов, животных или грузов, включая: деятельность (эксплуатацию) зданий и сооружений, таких как терминалы авиаперевозчиков и пр.;- управление аэропортами и воздушным движением;- наземное обслуживание на аэродромах и космодромахЭта группировка также включает:- тушение пожаров и предупреждение пожароопасных ситуаций на аэродромах и космодромах', 'H');
INSERT INTO `okvad` VALUES ('52.23.1', 'Деятельность вспомогательная, связанная с воздушным транспортом', 'H');
INSERT INTO `okvad` VALUES ('52.23.11', 'Деятельность аэропортовая', 'H');
INSERT INTO `okvad` VALUES ('52.23.12', 'Обеспечение обслуживания (управления) воздушного движения', 'H');
INSERT INTO `okvad` VALUES ('52.23.13', 'Выполнение авиационных работЭта группировка включает:- работы, выполняемые с использованием полетов гражданских воздушных судов в сельском хозяйстве, строительстве, для охраны окружающей среды, оказания медицинской помощи и других целей', 'H');
INSERT INTO `okvad` VALUES ('52.23.19', 'Деятельность вспомогательная прочая, связанная с воздушным транспортом', 'H');
INSERT INTO `okvad` VALUES ('52.23.2', 'Деятельность вспомогательная, связанная с космическим транспортом', 'H');
INSERT INTO `okvad` VALUES ('52.23.21', 'Деятельность наземных центров управления полетами космических объектов в космическом пространстве и центров (пунктов) космической связи', 'H');
INSERT INTO `okvad` VALUES ('52.23.22', 'Деятельность поисковых и аварийно-спасательных служб, в том числе по эвакуации спускаемых аппаратов (капсул), составных частей ракет космического назначения', 'H');
INSERT INTO `okvad` VALUES ('52.23.23', 'Деятельность, связанная с подготовкой космонавтов для работы непосредственно в космическом пространстве', 'H');
INSERT INTO `okvad` VALUES ('52.23.29', 'Деятельность вспомогательная прочая, связанная с космическим транспортом', 'H');
INSERT INTO `okvad` VALUES ('52.24', 'Транспортная обработка грузовЭта группировка включает:- погрузку и разгрузку грузов и багажа пассажиров независимо от вида транспорта, используемого для перевозки;- погрузку-выгрузку опасных грузов на железнодорожном транспорте;- стивидорную деятельность;- загрузку и разгрузку грузовых железнодорожных вагоновЭта группировка не включает:- деятельность терминалов, см. 52.21, 52.22 и 52.23', 'H');
