INSERT INTO `okvad` VALUES ('62.02.9', 'Деятельность консультативная в области компьютерных технологий прочая', 'J');
INSERT INTO `okvad` VALUES ('62.03', 'Деятельность по управлению компьютерным оборудованиемЭта группировка включает:- предоставление услуг по управлению на месте и эксплуатации компьютерных систем клиента и/или средств обработки данных, а также соответствующие услуги поддержки', 'J');
