INSERT INTO `okvad` VALUES ('68.20', 'Аренда и управление собственным или арендованным недвижимым имуществомЭта группировка включает:- сдачу в аренду и эксплуатацию собственного или арендованного недвижимого имущества: многоквартирных зданий и других жилых домов, квартир, нежилых зданий и помещений, включая выставочные залы и складские помещения, земельных участков;- предоставление в аренду домов, меблированных и немеблированных квартир или многоквартирных помещений, предназначенных для долговременного проживания, обычно на ежемесячной или ежегодной основеЭта группировка также включает:- строительство зданий для собственного пользования;- эксплуатацию стоянок для передвижных домов', 'L');
INSERT INTO `okvad` VALUES ('68.20.1', 'Аренда и управление собственным или арендованным жилым недвижимым имуществом', 'L');
