INSERT INTO `okvad` VALUES ('68.31', 'Деятельность агентств недвижимости за вознаграждение или на договорной основеЭта группировка включает:- предоставление агентствами услуг по работе с недвижимым имуществом: предоставление посреднических услуг в покупке, продаже и аренде недвижимого имущества, предоставление консультационных услуг при покупке, продаже и аренде недвижимого имущества;- деятельность агентов по поручительствуЭта группировка не включает:- деятельность в области права, см. 69.10', 'L');
