INSERT INTO `okvad` VALUES ('71.12.64', 'Государственный контроль (надзор) за соблюдением требований технических регламентов', 'M');
INSERT INTO `okvad` VALUES ('71.12.65', 'Федеральный государственный метрологический надзор', 'M');
INSERT INTO `okvad` VALUES ('71.12.66', 'Деятельность в области каталогизации продукции', 'M');
INSERT INTO `okvad` VALUES ('71.12.7', 'Кадастровая деятельность', 'M');
INSERT INTO `okvad` VALUES ('71.2', 'Технические испытания, исследования, анализ и сертификация', 'M');
INSERT INTO `okvad` VALUES ('71.20.1', 'Испытания и анализ состава и чистоты материалов и веществ: анализ химических и биологических свойств материалов и веществ; испытания и анализ в области гигиены питания, включая ветеринарный контроль и контроль за производством продуктов питания', 'M');
INSERT INTO `okvad` VALUES ('71.20.2', 'Испытания и анализ в области гигиены питания, включая ветеринарный контроль и контроль за производством продуктов питания', 'M');
INSERT INTO `okvad` VALUES ('71.20.3', 'Испытания и анализ физико-механических свойств материалов и веществ', 'M');
INSERT INTO `okvad` VALUES ('71.20.4', 'Испытания, исследования и анализ целостных механических и электрических систем, энергетическое обследование', 'M');
