INSERT INTO `okvad` VALUES ('77.3', 'Аренда и лизинг прочих машин и оборудования и материальных средств', 'N');
INSERT INTO `okvad` VALUES ('77.31', 'Аренда и лизинг сельскохозяйственных машин и оборудованияЭта группировка включает:- аренду сельскохозяйственных тракторов и машин и оборудования для сельского и лесного хозяйства без оператора, включая аренду машин, включенных в группировку 28.30Эта группировка не включает:- аренду сельскохозяйственных тракторов и других видов машин и оборудования для сельского и лесного хозяйства с оператором, см. 01.61, 02.40', 'N');
INSERT INTO `okvad` VALUES ('77.32', 'Аренда и лизинг строительных машин и оборудованияЭта группировка включает:- аренду строительных машин и оборудования без оператора, включая подъемные краны, строительные леса и рабочие платформы без их установки и демонтажаЭта группировка не включает:- аренду строительных машин и оборудования с оператором, см. 43', 'N');
INSERT INTO `okvad` VALUES ('77.33', 'Аренда и лизинг офисных машин и оборудования, включая вычислительную техникуЭта группировка включает:- прокат и аренду офисных машин и оборудования без оператора: вычислительной техники и вспомогательной вычислительной техники, копировально-множительных машин, пишущих машин и машин для обработки текста, бухгалтерских машин и оборудования: кассовых аппаратов, электронных калькуляторов и т.п., офисной мебели', 'N');
INSERT INTO `okvad` VALUES ('77.33.1', 'Аренда и лизинг офисных машин и оборудования', 'N');
INSERT INTO `okvad` VALUES ('77.33.2', 'Аренда и лизинг вычислительных машин и оборудования', 'N');
INSERT INTO `okvad` VALUES ('77.34', 'Аренда и лизинг водных транспортных средств и оборудованияЭта группировка включает:- прокат и аренду водных транспортных средств и оборудования без оператора, включая торговые и прочие судаЭта группировка не включает:- прокат и аренду водных транспортных средств и оборудования с оператором, см. 50;- прокат прогулочных катеров, см. 77.21', 'N');
INSERT INTO `okvad` VALUES ('77.35', 'Аренда и лизинг воздушных судов и авиационного оборудованияЭта группировка включает:- прокат и аренду воздушных транспортных средств без экипажа, включая самолеты и воздушные шарыЭта группировка не включает:- аренду воздушных транспортных средств с оператором, см. 51', 'N');
INSERT INTO `okvad` VALUES ('77.39', 'Аренда и лизинг прочих видов транспорта, оборудования и материальных средств, не включенных в другие группировкиЭта группировка включает:- прокат и аренду прочего автомобильного транспорта и оборудования, без оператора, используемых как промышленные средства производства: двигателей и турбин; станков, горного и нефтепромыслового оборудования, профессиональной радио- и телевизионной аппаратуры и аппаратуры связи, оборудования для производства кинофильмов, контрольно-измерительной аппаратуры, прочих машин и оборудования научного и промышленного назначения;- прокат и аренду сухопутных транспортных средств с оператором (кроме автомобилей): мотоциклов, автофургонов и прицепов для жилья и т.д., железнодорожного транспортаЭта группировка также включает:- аренду жилья и офисов;- аренду животных (например, стад, скаковых лошадей);- прокат и аренду контейнеров;- прокат и аренду поддонов', 'N');
